import React, { useState } from 'react';

function App() {
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [city, setCity] = useState('');
  const [games, setGames] = useState([]);
  const [error, setError] = useState(null);

  const handleSubmit = async (event) => {
    event.preventDefault();

    // Construct the data payload
    const payload = {
      start_date: startDate,
      end_date: endDate,
      city: city,
    };

    try {
      const response = await fetch('http://localhost:5000/date-team', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(payload),
      });

      if (!response.ok) {
        throw new Error('Failed to fetch games data');
      }

      const data = await response.json();
      setGames(data); // Set the games state with the received data
      setError(null); // Clear any existing error messages
    } catch (error) {
      setError('Error fetching data. Please try again.');
      setGames([]); // Clear the games data on error
    }
  };

  return (
    <div className="App">
      <h1>Game Schedule Finder</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Start Date:</label>
          <input
            type="date"
            value={startDate}
            onChange={(e) => setStartDate(e.target.value)}
            required
          />
        </div>
        <div>
          <label>End Date:</label>
          <input
            type="date"
            value={endDate}
            onChange={(e) => setEndDate(e.target.value)}
            required
          />
        </div>
        <div>
          <label>City:</label>
          <input
            type="text"
            value={city}
            onChange={(e) => setCity(e.target.value)}
            required
          />
        </div>
        <button type="submit">Submit</button>
      </form>

      {/* Error message display */}
      {error && <p style={{ color: 'red' }}>{error}</p>}

      {/* Display the JSON data */}
      {games.length > 0 && (
        <div>
          <h2>Game Schedule</h2>
          <ul>
            {games.map((game, index) => (
              <li key={index}>
                <strong>{game[1]}</strong> - {game[4]} vs {game[5]}<br />
                Date: {game[2]}, Time: {game[3]}<br />
                Venue: {game[6]}, City: {game[7]}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
}

export default App;
